import {AiTwotoneDelete} from "react-icons/ai"
import { useDispatch } from "react-redux";
import { toast } from "react-hot-toast";
import { remove } from "../redux/Slices/CartSlice";

const CartItem = ({item, itemIndex}) => {

  const dispatch = useDispatch();

  const removeFromCart= () =>{
    dispatch(remove(item.id));
    toast.success('removed Successfully');
  }
  return (
    <div className="max-w-2xl">

        <div className="flex">

          <div className="max-w-[180px]">
            <img src={item.image} className=" w-full"/>
          </div>

          <div className="flex flex-col gap-y-5">
            <h1 className="text-gray-700 font-semibold text-lg text-left truncate mt-1">{item.title}</h1>
            <p className="max-w-6xl ">{item.description}</p>
            <p>{item.price}</p>
          </div>

          <div onClick={removeFromCart} className="h-5 w-20 bg-red-200 text-red-500 rounded-full">
            <AiTwotoneDelete/>
          </div>
        </div>
        <div className="h-1 w-full bg-slate-900"></div>
    </div>
  );
};

export default CartItem;
