import {AiTwotoneDelete} from "react-icons/ai"

const CartItem = ({item, itemIndex}) => {
  return (
    <div>

        <div>

          <div>
            <img src={item.image}/>
          </div>

          <div>
            <h1>{item.title}</h1>
            <p>{item.description}</p>
            <p>{item.price}</p>
          </div>

          <div onClick={removeFromCart}>
            <AiTwotoneDelete/>
          </div>
        </div>
    </div>
  );
};

export default CartItem;
